import React, { useEffect, useState } from 'react';
import { Button, Image, Text, View } from 'react-native';
import LoadingScreen from '../../components/indicator/LoadingScreen';
import FormTextInput from '../../components/input/FormTextInput';
import AppText from '../../components/text/AppText';
import { useAppDispatch, useAppSelecter } from '../../store/configureStore';
import colors from '../../styles/colors';
import { styles } from '../../styles/styles';
import { MaterialIcons } from '@expo/vector-icons';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import * as Yup from 'yup';
import RNPickerSelect from 'react-native-picker-select';
import * as ImagePicker from 'expo-image-picker';




import AppButton from '../../components/button/AppButton';
import { fetchInvoiceDetailsAsync } from '../../reducers/invoiceSlice';
import { Formik } from 'formik';
import { fetchModeOfPaymentsAsync } from '../../reducers/modeOfPaymentSlice';


const PaymentFormScreen = () => {
    const dispatch = useAppDispatch()
    const { tenant } = useAppSelecter((state) => state.tenant)
    const { invoice, isFetchingInvoiceDetails } = useAppSelecter((state) => state.invoice)
    const { modeOfPayments } = useAppSelecter(state => state.modeOfPayment);

    const { bg_light, container, my_5, p_5 } = styles
    const { darkGrey, primary } = colors

    useEffect(() => {
        dispatch(fetchModeOfPaymentsAsync())
    }, [])

    useEffect(() => {
        if (!invoice) fetchInvoiceDetailsAsync(tenant?.tenantUniqueId!)
    }, [invoice])



    const [payment, setPayment] = useState({
        modeOfPaymentId: "",
        amount: 0,
        file: ""
    })

    // Image Picker
    const [image, setImage] = useState(null);

    const pickImage = async () => {
        let result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.All,
            allowsEditing: true,
            aspect: [4, 3],
            quality: 1,
        });

        console.log(result);
    };

    const validationSchema = Yup.object().shape({
        modeOfPayment: Yup.string().required().label('Mode of Payment'),
        amount: Yup.number().required().label('Amount'),
        file: Yup.string().required().label('Proof of Payment'),
    });



    const onSubmit = async (values: any) => {
        console.log(values)
    }

    if (isFetchingInvoiceDetails || !invoice) return <LoadingScreen />
    const { invoiceNumber } = invoice


    return (
        <View style={container}>

            <View style={[my_5]}>
                <AppText as="h5" bold color={darkGrey} >Invoice Number</AppText>
                <AppText bold >{invoiceNumber}</AppText>
            </View>

            <Formik
                initialValues={payment}
                onSubmit={values => onSubmit(values)}
                validationSchema={validationSchema}
            >
                {
                    ({ handleChange, setFieldTouched, errors, touched }) => (
                        <>
                            <View style={my_5}>
                                <AppText as="h5" bold color={darkGrey} >Mode of Payment</AppText>
                                <View style={[bg_light, my_5, p_5]} >
                                    <RNPickerSelect
                                        onValueChange={(value) => setModeOfPaymentId(value)}
                                        items={modeOfPayments.map(i => {
                                            return { label: i.bankName, value: i.id }
                                        })
                                        }
                                    />
                                </View>
                            </View>

                            <FormTextInput
                                icon={<MaterialIcons name="money" size={20} color={primary} />}
                                onBlur={() => setFieldTouched('amount')}
                                onChangeText={handleChange('amount')}
                                label='Amount'
                                placeholder='Amount'
                                errorMessage={touched && errors.amount}
                            />

                            <View style={my_5}>
                                <AppText as="h5" bold color={darkGrey} >Mode of Payment</AppText>
                                <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
                                    <Button title="Pick an image from camera roll" onPress={pickImage} />
                                    {image && <Image source={{ uri: image }} style={{ width: 200, height: 200 }} />}
                                </View>
                            </View>
                        </>
                    )
                }
            </Formik>

            <AppButton onPress={() => { }} title='Submit' />

        </View>
    );
}

export default PaymentFormScreen;
