import React from 'react';
import Constants from 'expo-constants';
import { StyleSheet, View } from 'react-native';
import { Entypo } from '@expo/vector-icons';
import { FontAwesome } from '@expo/vector-icons';

import colors from '../../styles/colors';
import AppText from '../../components/text/AppText';
import { MaterialIcons } from '@expo/vector-icons';
import { MaterialCommunityIcons } from '@expo/vector-icons';

const HomeScreen = () => {
    return (
        <View style={styles.container}>
            <AppText bold color={colors.darkGrey}>Hi, Name</AppText>
            <AppText bold>ACCNTNMBR</AppText>

            <View style={styles.card}>
                <View style={styles.card_title}>
                    <AppText as="h5" color={colors.light}>Total Balance</AppText>
                    <AppText as="h3" bold color={colors.light}>PHP 4,000</AppText>
                </View>

                <View style={styles.row}>
                    <View style={styles.card_content}>
                        <View style={styles.card_icon}>
                            <Entypo name="location-pin" size={15} color={colors.light} />
                        </View>

                        <View>
                            <AppText as="h5" color={colors.light}>Slot Number</AppText>
                            <AppText as="h5" bold color={colors.light}>A-002</AppText>
                        </View>
                    </View>

                    <View style={styles.card_content}>
                        <View style={styles.card_icon}>
                            <FontAwesome name="calendar-o" size={15} color={colors.light} />
                        </View>

                        <View>
                            <AppText as="h5" color={colors.light}>Nexy Billing Date</AppText>
                            <AppText as="h5" bold color={colors.light}>Mar 07, 2022</AppText>
                        </View>
                    </View>
                </View>
            </View>

            <View style={[styles.row_center_x, styles.bg_light, styles.p_5]}>
                <View style={styles.card_icon}>
                    <MaterialCommunityIcons name="contactless-payment-circle" size={24} color={colors.light} />
                </View>
                <View>
                    <AppText as="h4" bold>PAYMENTS</AppText>
                    <AppText as="h5" bold color={colors.darkGrey}>View payment history</AppText>
                </View>
            </View>

        </View>

    );
}

const styles = StyleSheet.create({
    bg_primary: {
        backgroundColor: colors.primary,
    },

    bg_light: {
        backgroundColor: colors.light
    },

    row: {
        display: 'flex',
        flexDirection: 'row'
    },

    row_center_x: {
        alignItems: 'center',
        display: 'flex',
        flexDirection: 'row',
    },

    p_5: {
        padding: 15,
    },





    container: {
        marginBottom: 15,
        marginHorizontal: 15,
        marginTop: Constants.statusBarHeight + 15,
    },

    card: {
        backgroundColor: colors.dark,
        borderRadius: 15,
        paddingHorizontal: 15,
        paddingVertical: 25,
        marginVertical: 15
    },

    card_title: {
        alignItems: "center",
        marginBottom: 15,
    },

    card_content: {
        alignItems: 'center',
        display: 'flex',
        flexDirection: 'row',
        width: '50%',
    },

    card_icon: {
        alignItems: 'center',
        display: 'flex',
        backgroundColor: colors.secondary,
        borderRadius: 12.5,
        justifyContent: 'center',
        height: 25,
        marginRight: 7,
        width: 25,
    }
})

export default HomeScreen;
