import React from 'react';
import { TextInput, View } from 'react-native';

import colors from '../../styles/colors';
import { styles } from '../../styles/styles';
import AppText from '../text/AppText';
import { MaterialIcons } from '@expo/vector-icons';

interface Props {
    icon: React.ReactNode,
    label?: string,
    placeholder: string,
    value?: string,
}


const FormTextInput: React.FC<Props> = ({ icon, label, placeholder, value }) => {

    const { bg_light, center_x, my_5, p_5, rounded, row_center_x, w_25 } = styles
    const { darkGrey, primary, red } = colors

    return (
        <View style={my_5}>
            <AppText as="h5" bold color={darkGrey}>{label}</AppText>
            <View style={[bg_light, my_5, p_5, row_center_x]} >
                <View style={[center_x, w_25]}>
                    {icon}
                </View>

                <TextInput placeholder={placeholder} value={value} />
            </View>

            <View style={[bg_light, row_center_x, p_5]}>
                <MaterialIcons name="error" size={20} color={red} />
                <AppText as='h5' bold italic color={red}>Error Message</AppText>
            </View>

        </View>
    );
}


export default FormTextInput;
