import React from 'react';
import { StyleSheet, TextInput, View } from 'react-native';
import colors from '../../styles/colors';
import { styles } from '../../styles/styles';
import AppText from '../text/AppText';
import { AntDesign } from '@expo/vector-icons';

interface Props {
    label: string,
    placeholder: string,
}

const FormTextInput: React.FC<Props> = ({ label, placeholder }) => {

    const { bg_light, container, my_5, p_5, row } = styles
    const { darkGrey, primary } = colors

    return (
        <View style={my_5}>
            <AppText as="h5" bold color={darkGrey}>{label}</AppText>
            <View style={[bg_light, my_5, p_5, row]} >
                <AntDesign name="stepforward" size={24} color={primary} />
                <TextInput placeholder={placeholder} />
            </View>
        </View>
    );
}


export default FormTextInput;
