import React, { useEffect } from 'react';
import { View } from 'react-native';
import LoadingScreen from '../../components/indicator/LoadingScreen';
import Detail from '../../components/item/Detail';
import { fetchInvoiceDetailsAsync } from '../../reducers/invoiceSlice';
import { useAppSelecter } from '../../store/configureStore';
import { styles } from '../../styles/styles';
import { dateFormatter } from '../../utils/dateFormatter';

const PaymentReceiptScreen = ({ }) => {

    const { tenant } = useAppSelecter((state) => state.tenant)
    const { paymentResult } = useAppSelecter((state) => state.invoice)

    const { container, flex_1 } = styles


    if (!paymentResult) return <LoadingScreen />

    const { amount, dateCreated, referenceNumber } = paymentResult!

    return (
        <View style={[container, flex_1]}>
            <Detail title='Date' value={dateFormatter(dateCreated)} />
            <Detail title='Amount' value={amount} />
            <Detail title='Reference Number' value={referenceNumber} />
        </View>
    );
}

export default PaymentReceiptScreen;
