import React, { useEffect } from 'react';
import { FlatList, Text, TouchableOpacity, View } from 'react-native';
import colors from '../../styles/colors';
import { styles } from '../../styles/styles';
import { FontAwesome } from '@expo/vector-icons';
import AppText from '../../components/text/AppText';
import { useAppDispatch, useAppSelecter } from '../../store/configureStore';
import { useDispatch } from 'react-redux';
import { fetchInvoicesAsync } from '../../reducers/invoiceSlice';
import LoadingScreen from '../../components/indicator/LoadingScreen';
import NoData from '../../components/indicator/NoData';
import { dateFormatter } from '../../utils/dateFormatter';

interface Props {
    navigation: any
}

const InvoicesScreen: React.FC<Props> = ({ navigation }) => {
    const dispatch = useAppDispatch()
    const { tenant } = useAppSelecter((state) => state.tenant)
    const { invoices, isFetchingInvoices, isFetchingInvoiceDetails } = useAppSelecter((state) => state.invoice)

    useEffect(() => {
        if (!tenant) navigation.navigate("AuthNavigator")
    }, [tenant])

    useEffect(() => {
        dispatch(fetchInvoicesAsync(tenant?.tenantUniqueId!))
    }, [])

    const { badge, bg_light, bg_red, bg_yellow, container, me_5, my_5, p_15, row_center_x, row_center_x_between, rounded } = styles;
    const { blue, darkGrey, green, light, secondary } = colors;

    const renderInvoiceStatus = (status: number) => {
        if (status === 0) return
        (
            <View style={[badge, bg_red]}>
                <AppText as="h5" color={light}>Unpaid</AppText>
            </View>
        )

        if (status === 1) return
        (
            <View style={[badge, bg_yellow]}>
                <AppText as="h5" color={light}>Pending</AppText>
            </View>
        )

        if (status === 2) return
        (
            <View style={[badge, bg_yellow]}>
                <AppText as="h5" color={blue}>Partially Paid</AppText>
            </View>
        )

        return
        (
            <View style={[badge, bg_yellow]}>
                <AppText as="h5" color={blue}>Partially Paid</AppText>
            </View>
        )
    }

    if (isFetchingInvoices) return <LoadingScreen />
    if (!invoices) return <NoData />

    return (
        <View style={container}>
            <FlatList
                data={invoices}
                keyExtractor={(i) => i.id}
                renderItem={({ item }) =>
                    <TouchableOpacity onPress={() => handleInvoiceDetails()}>

                        <View style={[bg_light, my_5, p_15, rounded]}>
                            <View style={row_center_x}>
                                <View style={me_5}>
                                    <AppText as="h5" bold color={darkGrey}>{dateFormatter(item.dateCreated)}</AppText>
                                </View>

                                <AppText as="h5" bold>{renderInvoiceStatus(item.invoiceStatus)}</AppText>
                            </View>

                            <View style={row_center_x_between}>
                                <View>
                                    <AppText bold>{item.invoiceNumber}</AppText>
                                </View>

                                <AppText as="h3" bold color={secondary}>{item.amount}</AppText>
                            </View>
                        </View>
                    </TouchableOpacity>
                }
            />
        </View>
    );
}

export default InvoicesScreen;

