import React, { useEffect } from 'react';
import { Text, View } from 'react-native';
import AppText from '../../components/text/AppText';
import { styles } from '../../styles/styles';
import { useAppDispatch, useAppSelecter } from '../../store/configureStore';
import { useDispatch } from 'react-redux';
import { fetchAnnouncementDetailsAsync } from '../../reducers/announcementSlice';
import LoadingScreen from '../../components/indicator/LoadingScreen';

interface Props {
    navigation: any,
    route: any
}

const AnnouncementDetailsScreen: React.FC<Props> = ({ navigation, route }) => {
    const { tenant } = useAppSelecter((state) => state.tenant)
    const dispatch = useAppDispatch()

    const { announcement, isFetchingAnnouncementDetails } = useAppSelecter((state) => state.announcement)
    const { announcementId } = route.params;

    useEffect(() => {
        if (!tenant) navigation.navigate("AuthNavigator")
    }, [tenant])

    if (!announcement) return <LoadingScreen />

    const { title, message, dateCreated } = announcement

    const { bg_light, flex_1 } = styles;

    return (
        <View style={bg_light, flex_1}>
            <AppText bold>{title}</AppText>
            <AppText>{dateCreated}</AppText>
            <AppText>{message}</AppText>
        </View>
    );
}

export default AnnouncementDetailsScreen;
