import React from 'react';
import { Text, View } from 'react-native';
import InvoiceBadge from '../../components/badge/InvoiceBadge';
import LoadingScreen from '../../components/indicator/LoadingScreen';
import Detail from '../../components/item/Detail';
import AppText from '../../components/text/AppText';
import { useAppDispatch, useAppSelecter } from '../../store/configureStore';
import colors from '../../styles/colors';
import { styles } from '../../styles/styles';
import { dateFormatter } from '../../utils/dateFormatter';


const InvoiceDetailsScreen = () => {


    const dispatch = useAppDispatch()
    const { invoice, isFetchingInvoiceDetails } = useAppSelecter((state) => state.invoice)

    const { amount, dateCreated, dueDate, invoiceNumber, invoiceStatus, } = invoice!

    const { badge, bg_light, bg_green, bg_red, container, p_5, p_15, row_center_x_between, rounded } = styles;
    const { light } = colors


    if (isFetchingInvoiceDetails) return <LoadingScreen />

    return (
        <View style={[bg_light, container, p_15, rounded]}>

            <View style={[row_center_x_between,]}>
                <View>
                    <AppText as="h3" bold>{`INV-${invoiceNumber}`} </AppText>
                    <AppText as="h2" bold>{amount} </AppText>
                </View>

                {/* <View style={{ display: 'flex', flexDirection: 'row', justifyContent: "flex-end" }}> */}
                <InvoiceBadge status={invoiceStatus} />
                {/* </View> */}
            </View>


            <View style={{ display: 'flex', flexDirection: 'row', justifyContent: "flex-end" }}>
            </View>

            <View>
                <AppText as="h5" bold>Date:</AppText>
                <AppText as="h5" bold>Due Date:</AppText>
            </View>

            <Detail title="Date Created" value={dateFormatter(dateCreated)} />
            <Detail title="Due Date" value={dateFormatter(dueDate)} />

            {/* <Detail title="Invoice Status" value={<InvoiceBadge status={invoiceStatus} />} />
            <Detail title="Invoice Number" value={invoiceNumber} />
            <Detail title="Date Created" value={dateFormatter(dateCreated)} />
            <Detail title="Due Date" value={dateFormatter(dueDate)} />
            <Detail title="Amount" value={amount} /> */}


        </View>
    );
}

export default InvoiceDetailsScreen;
